import React, { useState } from "react";
import styled from "styled-components";

import Heading from "../components/Heading";
import InputGroup from "../components/InputGroup";
import ShipList from "../components/ShipList";
import { Ship } from "../store/types";

const Container = styled.div`
  background-color: #141414;
  min-height: 100vh;
  overflow-x: hidden;
  overflow-y: scroll;
  position: relative;

  .form {
    padding: 10%;
    position: absolute;
    transition: all 1s ease-in-out;
    &.open {
      left: 0;
    }

    &.closed {
      left: 5000px;
    }
  }

  .list {
    width: 100%;
    padding: 10%;

    transition: all 1s ease-in-out;
    position: absolute;
    &.open {
      right: 0;
    }

    &.closed {
      right: 5000px;
    }
  }
`;

const Home = () => {
  const [value, setValue] = useState("");
  const [step, setStep] = useState<"form" | "result">("form");
  const ships: Ship[] = [
    // @ts-ignore
    {
      imageURL:
        "https://cdn.shopify.com/s/files/1/0340/2849/products/Disc-StarWars-DeathStar_651a2d63-1b09-4d53-b69d-09b2feeae155_810x.jpg?v=1582135686",
      name: "Death Star",
      stops: 40,
    },
    // @ts-ignore
    {
      imageURL:
        "https://static.wikia.nocookie.net/starwars/images/2/24/TantiveIV-TSWB.png/revision/latest?cb=20201021033048",
      name: "CR90 corvette",
      stops: 40,
    },

    // @ts-ignore
    {
      imageURL:
        "data:image/jpeg;base64,",
      name: "Star Destroyer",
      stops: 40,
    },
    // @ts-ignore
    {
      name: "Sentinel-class landing craft",
      imageURL:
        "data:image/jpeg;base64,",
      stops: 40,
    },
    // @ts-ignore
    {
      imageURL:
        "https://cdn.shopify.com/s/files/1/0340/2849/products/Disc-StarWars-DeathStar_651a2d63-1b09-4d53-b69d-09b2feeae155_810x.jpg?v=1582135686",
      name: "Death Star",
      stops: 40,
    },
    // @ts-ignore
    {
      imageURL:
        "https://static.wikia.nocookie.net/starwars/images/2/24/TantiveIV-TSWB.png/revision/latest?cb=20201021033048",
      name: "CR90 corvette",
      stops: 40,
    },

    // @ts-ignore
    {
      imageURL:
        "data:image/jpeg;base64,",
      name: "Star Destroyer",
      stops: 40,
    },
    // @ts-ignore
    {
      stops: 40,
      name: "Sentinel-class landing craft",
      imageURL:
        "data:image/jpeg;base64,",
    },
  ];

  const onChangeValue = (event: React.ChangeEvent<HTMLInputElement>) =>
    setValue(event.target.value);

  const handleAction = () => setStep("result");

  return (
    <Container>
      <div className={step === "result" ? "form closed" : "form open"}>
        <Heading className="headline">Calculate your adventure!</Heading>
        <InputGroup
          type="number"
          value={value}
          onChange={onChangeValue}
          label="How far, far away?"
          actionLabel="Go"
          action={handleAction}
        />
      </div>
      <div className={step === "result" ? "list open" : "list closed"}>
        <img
          src="./icons/back.png"
          alt="Back Button"
          onClick={() => setStep("form")}
        />
        <ShipList data={ships} />
      </div>
    </Container>
  );
};

export default Home;
